import { useState } from "react";
import * as BooksApi from "../src/BooksAPI";
import BookShelf from "./BookShelf";

const ListBook = ({ books }) => {
  const [currentlyReadingBook, setCurrentlyReadingBook] = useState(
    books.filter((b) => b.shelf === "currentlyReading")
  );

  const [wantToRead, setWantToRead] = useState(
    books.filter((b) => b.shelf === "wantToRead")
  );

  const [read, setRead] = useState(books.filter((b) => b.shelf === "read"));

  const changeCategory = (e, b) => {
    var v = e.target.value;
    b.shelf === "currentlyReading" &&
      setCurrentlyReadingBook(
        currentlyReadingBook.filter((book) => book.id !== b.id)
      );
    b.shelf === "wantToRead" &&
      setWantToRead(wantToRead.filter((book) => book.id !== b.id));
    b.shelf === "read" && setRead(read.filter((book) => book.id !== b.id));
    console.log(e.target.value, b);
    BooksApi.update(b, v);
    v === "currentlyReading" &&
      setCurrentlyReadingBook(currentlyReadingBook.concat(b));
    v === "wantToRead" && setWantToRead(wantToRead.concat(b));
    v === "read" && setRead(read.concat(b));
  };

  return (
    <div className="list-books">
      <div className="list-books-title">
        <h1>MyReads</h1>
      </div>
      <div className="list-books-content">
        <div>
          <div className="bookshelf">
            <h2 className="bookshelf-title">Currently Reading</h2>
            <BookShelf books = {currentlyReadingBook} onChangeCategory = {changeCategory}/>
          </div>
          <div className="bookshelf">
            <h2 className="bookshelf-title">Want to Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {wantToRead.map((b) => {
                  return (
                    <li>
                      <div className="book">
                        <div className="book-top">
                          <div
                            className="book-cover"
                            style={{
                              width: 128,
                              height: 193,
                              backgroundImage: `url(${b.imageLinks.thumbnail})`,
                            }}
                          ></div>
                          <div className="book-shelf-changer">
                            <select
                              value={b.shelf}
                              onChange={(e) => changeCategory(e, b)}
                            >
                              <option value="none" disabled>
                                Move to...
                              </option>
                              <option value="currentlyReading">
                                Currently Reading
                              </option>
                              <option value="wantToRead">Want to Read</option>
                              <option value="read">Read</option>
                              <option value="none">None</option>
                            </select>
                          </div>
                        </div>
                        <div className="book-title">{b.title}</div>
                        <div className="book-authors">{b.authors}</div>
                      </div>
                    </li>
                  );
                })}
              </ol>
            </div>
          </div>
          <div className="bookshelf">
            <h2 className="bookshelf-title">Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {read.map((b) => {
                  return (
                    <li>
                      <div className="book">
                        <div className="book-top">
                          <div
                            className="book-cover"
                            style={{
                              width: 128,
                              height: 193,
                              backgroundImage: `url(${b.imageLinks.thumbnail})`,
                            }}
                          ></div>
                          <div className="book-shelf-changer">
                            <select
                              value={b.shelf}
                              onChange={(e) => changeCategory(e, b)}
                            >
                              <option value="none" disabled>
                                Move to...
                              </option>
                              <option value="currentlyReading">
                                Currently Reading
                              </option>
                              <option value="wantToRead">Want to Read</option>
                              <option value="read">Read</option>
                              <option value="none">None</option>
                            </select>
                          </div>
                        </div>
                        <div className="book-title">{b.title}</div>
                        <div className="book-authors">{b.authors}</div>
                      </div>
                    </li>
                  );
                })}
              </ol>
            </div>
          </div>
        </div>
      </div>
      <div className="open-search">
        <a onClick={() => {}}>Add a book</a>
      </div>
    </div>
  );
};

export default ListBook;
